        }

        EventBus.on("logout", () => {
            logOut();
        });

        return () => {
            EventBus.remove("logout");
        };
    }, []);

    const logOut = () => {
        AuthService.logout();
        setCurrentUser(undefined);
    };
  return (
      <BrowserRouter>
      <Routes>
        <Route path="/" element={<Login />} />
          {currentUser ? (
              <Route path="/dashboard*" element={<Dashboard />} />
          ) : (
              <Route path="/" element={<Login />} />
          )}
          <Route path="*" element={<Login />} />
      </Routes>
    </BrowserRouter>
  );
};

export default App;
