import React, {useEffect, useState, Component} from "react";
import logo from '../logo.svg';
import {useNavigate, useLocation} from "react-router-dom";
import AuthService from "../services/auth.service";
import {useCookies} from 'react-cookie';

export default function Dashboard(props) {

    const {state} = useLocation();
    const [userReady, setUserReady] = useState(() => state['Username'])
    const [redirect, setRedirect] = useState(() => state['UID'])
    const [cookies, setCookie] = useCookies(['session']);

    const navigate = useNavigate();

    const currentUser = AuthService.getCurrentUser();


    AuthService.getCurrentUser().then(
        (response) => {
            navigate("/", {state: response});
            window.location.reload();
        },
        (error) => {
            const resMessage =
                (error.response &&
                    error.response.data &&
                    error.response.data.message) ||
                error.message ||
                error.toString();

        }
    );


    this.setState({currentUser: currentUser, userReady: true})

    if (this.state.redirect) {
        navigate("/");
    }

    return (<div className="App">
        {(this.state.userReady) ? <header className="App-header">
            <img src={logo} className="App-logo" alt="logo"/>
            <p>
                Edit <code>src/App.js</code> and save to reload.
            </p>
            <a
                className="App-link"
                href="https://reactjs.org"
                target="_blank"
                rel="noopener noreferrer"
            >
                Learn React
            </a>
            <p>Username: {currentUser.username}</p>
            <p>Token: {currentUser.accessToken}
                {currentUser.accessToken.substring(0, 20)} ...{" "}
                {currentUser.accessToken.substring(currentUser.accessToken.length - 20)}</p>
        </header> : null}
    </div>)
}
